            for (int j = i + 1; j < line.length; j++) {
                if (i == j) continue;
                long A = line[i][0];
                long B = line[i][1];
                long E = line[i][2];
                long C = line[j][0];
                long D = line[j][1];
                long F = line[j][2];

                long down = A * D - B * C;
                long ux = B * F - E * D;
