        }

        @Override
        public int hashCode() {
            return Objects.hash(y, x);
        }

        @Override
        public int compareTo(Pair o) {
            if (y == o.y) {
                return x - o.x;
            }
            return Integer.compare(y, o.y);
        }
    }

