    }

    private String translateNoSharp(String score) {
        score = score.replaceAll("C#", "c");
        score = score.replaceAll("D#", "d");
        score = score.replaceAll("F#", "f");
        score = score.replaceAll("G#", "g");
        score = score.replaceAll("A#", "a");
        return score;
    }
